const audio=new Audio,tracksCard=document.getElementsByClassName("track"),player=document.querySelector(".player"),pauseBtn=document.querySelector(".player__icon_pause"),stopBtn=document.querySelector(".player__icon_stop"),playMusic=a=>{const e=a.currentTarget;audio.src=e.dataset.track,audio.play(),pauseBtn.classList.remove("player__icon_play"),player.classList.add("player_active");for(let a=0;a<tracksCard.length;a++)tracksCard[a].classList.remove("track_active");e.classList.add("track_active")};for(let a=0;a<tracksCard.length;a++)tracksCard[a].addEventListener("click",playMusic);pauseBtn.addEventListener("click",()=>{audio.paused?(audio.play(),pauseBtn.classList.remove("player__icon_play")):(audio.pause(),pauseBtn.classList.add("player__icon_play"))}),stopBtn.addEventListener("click",()=>{});